mapper.select:
SELECT dt.id,dt.userId,dt.userGoodsId,dt.content,dt.status, CONCAT(dt.time,'') as time,u.realName FROM dt_comment dt JOIN tab_user u ON dt.userId = u.id where 1=1
AND dt.status = #{status}
AND dt.userId = #{userId}
GROUP BY dt.id ORDER BY dt.time desc limit ${offset},${pageSize}

SELECT count(id) from dt_comment dt
where 1=1

    AND dt.userId = #{userId}


    AND dt.status = #{status}

mapper.update:
update dt_comment set

    content = #{content}

where id = #{id}
AND userId =#{userId}

mapper.insert:
insert  into dt_comment(userId, userGoodsId, content, time, status)
values
(#{userId},#{userGoodsId},#{content},CURRENT_TIMESTAMP(),#{status});

mapper.update:
update dt_comment set
status = #{status}
where id = #{id};
