mapper.select:
SELECT * from tab_user
where

    type is null


    type = #{type}


    AND groupLeaderId = #{groupLeaderId}

limit ${offset},${pageSize}

SELECT count(id) from tab_user where
type is null
type = #{type}

mapper.insert:

    SELECT LAST_INSERT_ID() AS id

insert into tab_user(userName,password,realName,active,sex,IDNumber,avatar,type,contact)
values(#{userName},#{password},#{realName},#{active},#{sex},#{IDNumber},#{avatar},#{type},#{contact});


    SELECT LAST_INSERT_ID() AS id

insert into dt_user_address(address,consignee,isDefault,phoneNumber,userId)
values(#{address.address},#{address.consignee},#{address.isDefault},#{address.phoneNumber},#{address.userId});
