mapper.select:
SELECT id,number,DATE_FORMAT(TIME,'%Y-%m-%d %H:%i:%s') as time,
userId,addressId,totalPrice from dt_order
WHERE 1 = 1

    AND flag = #{flag}


    AND userId = #{userId}

SELECT count(id) from dt_order
WHERE 1 = 1

    AND flag = #{flag}


    AND userId = #{userId}

mapper.update:
update dt_order set status = #{status}

    ,flag=#{flag}

where id = #{id};

mapper.insert:

    SELECT LAST_INSERT_ID() AS id

insert  into dt_order(number,userId,addressId,totalPrice,status,time)
values(#{name},#{userId},#{addressId},#{totalPrice},#{status},CURRENT_TIMESTAMP );
